#include <iostream>
#include <stdlib.h>
#include <ctime>

using namespace std;

int main()
{

    int i = 0;
    double winnings = 0;
    int bet = 1;
    int damnCount = 0;
    srand(time(0));

    for(int i = 0; i < 100; i++) {

    while (i < 1000000)
    {

        

        double winChance = 48.;
        double loseChance = 100 - winChance;

        

        int roll2 = rand() % 100;

        if (roll2 >= winChance)
        {
            winnings = winnings - bet;
            bet = bet * 2;
            if (bet > 1000) {
                cout << "max reached" << endl;
                damnCount += 1;
                break;
            }
        }
        else
        {

            winnings += bet;
            bet = 1;
        }

        i++;
        cout << winnings << endl;

    }
    }

    cout << damnCount << endl;
    cout << "rolls: " << i + 1 << endl;

    return 0;
}
